namespace Ch13GreaterThanInterface {     internal class Program     {         private static void Main(string[] args)         {             Console.WriteLine("Ch. 13 Console Project: Greater Than Interface by Brandon Phillips");              // hardcoded employees             EmployeePayroll emp0 = new EmployeePayroll(1234, 40, 10.50);             EmployeePayroll emp1 = new EmployeePayroll(9876, 40, 12.00);              Console.WriteLine("\nComparing payroll objects");              // display employees             Console.WriteLine(emp0.ToString());             Console.WriteLine(emp1.ToString());              // compare employees             if (emp0.IsGreater(emp1))             {                 emp0.DisplayGreater(emp1);             }             else             {                 emp1.DisplayGreater(emp0);             }              // update hours to change results             emp0.SetHours(50);             Console.WriteLine("\nComparing with updated hours");              // display employees             Console.WriteLine(emp0.ToString());             Console.WriteLine(emp1.ToString());              // compare employees             if (emp0.IsGreater(emp1))             {                 emp0.DisplayGreater(emp1);             }             else             {                 emp1.DisplayGreater(emp0);             }              // create inventory items             Inventory inv0 = new Inventory(545678, 100, "USB cable", 2.99);             Inventory inv1 = new Inventory(919293, 100, "Flash Drive", 8.99);              Console.WriteLine("\nComparing inventory items");              // display inventory items             Console.WriteLine(inv0.ToString());             Console.WriteLine(inv1.ToString());              // compare items             if (inv0.IsGreater(inv1))             {                 inv0.DisplayGreater(inv1);             }             else             {                 inv1.DisplayGreater(inv0);             }              // update qty             inv1.SetQty(20);             Console.WriteLine("\nComparing with updated quantity on hand");              // display inventory items             Console.WriteLine(inv0.ToString());             Console.WriteLine(inv1.ToString());              // compare items             if (inv0.IsGreater(inv1))             {                 inv0.DisplayGreater(inv1);             }             else             {                 inv1.DisplayGreater(inv0);             }              // exit program             Console.WriteLine("\nPress any key to exit...");             Console.ReadKey();         }     } }